import styled from "styled-components";
import zap from "../assets/7c81bd56-d313-48fc-8857-426e4f05d713.jpg"
import Fachada from "../assets/laptop-wallet-iphone-pen-wallpaper-preview.jpg"
import civil1 from "../assets/civil.jpg"
import civil2 from "../assets/civil2.jpg"


export const Foto = styled.img`
    width:100%;
    height: 600px;
    display: flex;
    align-self: center;
    background-image: url(${Fachada}); 
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover;
    background-color: #F1AC88;
`;

export const Container = styled.div`
    width: 100%;
    height: auto;
`;


export const NomeContainer = styled.div`
    width: auto;
    height: auto;
    font-size: 32px;
    font-weight: bold;
    color: #783937;
    padding-top: 14px;
`;

export const InfoContainer = styled.div`
    width: 100%;
    height: 630px;
    background-color: #F1AC88;
`;

export const FotoInfo = styled.div`
    width: 100%;
    height: auto;
    display: flex;
    background-color: #F1AC88;
    align-items: center;
    justify-content: center;
`;

export const FotoTamiao = styled.div`
    width: 400px;
    height: 400px;
    color: #fff;
    background-image: url(${zap});
    margin-right: 400px;
    border-radius: 50%;
    border: 5px solid #2d1c16;
    transition: transform .2s;
    margin-top: 40px;
    background-position: 0%, 0%;
    background-size: cover;
    &:hover {
        transform: scale(1.1);
    }
`;

export const TextoTamiao = styled.div`
    width: 600px;
    height: 300px;
    font-size: 22px;
    font-weight: bold;
    color: #783937;
`;

export const ServiceContainer = styled.div`
    width: 100%;
    height: 900px;
    background-image: url(${civil1});
    background-attachment: fixed;
    background-position: center;
    background-repeat: no-repeat;
    background-size: cover
`;

export const ServiceTitle = styled.div`
    width: auto;
    height: auto;
    font-size: 44px;
    font-weight: bold;
    color: #fff;
    padding-top: 20px;
`;
