import styled from "styled-components";
import logo from "../assets/logodireito.png"
import he from "../assets/he.jpeg"

export const Container = styled.header`
    width: 100%;
    height: 80px;
    background-color: #000000;
    padding: 0 16px;
    box-sizing: border-box;
    display: flex;
    align-items: center;
    justify-content: center;
`;

export const GuiasContainer = styled.div`
    width: auto;
    height: auto;
    align-items: center;
    justify-content: space-between;
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    column-gap: 30px;
    row-gap: 50px;
   
`;

export const Início = styled.div`
    font-size: 26px;
    font-weight: bold;
    color: #fff;
    transition: .5s;
    &:hover {
        color: red;
    }
`;

export const AndréTamião = styled.div`
    font-size: 26px;
    font-weight: bold;
    color: #fff;
    transition: .5s;
    &:hover {
        color: red;
    }
`;

export const Logo = styled.div`
    width: 95px;
    height: 95px;
    display: flex;
    background-image: url(${logo});
    transition: 3s;
    &:hover {
        background-image: url(${he});
    }
`;
