import styled from "styled-components";
import zap from "../assets/7c81bd56-d313-48fc-8857-426e4f05d713.jpg"

export const Foto = styled.img`
    width: 1920px;
    height: 600px;
    display: flex;
    align-self: center;
    background-image: url("src/assets/wallpaper dmc.jpg"); 
`;

export const Container = styled.div`
    width: 100%;
    height: auto;
`;

export const InfoContainer = styled.div`
    width: 1920px;
    height: 600px;
    display: grid;
    background-color: black;
    align-items: center;
    justify-content: center;
`;

export const FotoTamiao = styled.div`
    width: 400px;
    height: 500px;
    color: #fff;
    background-image: url(${zap});
    border: 4px #fff solid;
    padding: 2% 4% 2% 4%;
`;

export const TextoTamiao = styled.div`
    width: 600px;
    height: 300px;
    background-color: blue;
    font-size: 18px;
    font-weight: bold;
    color: #fff
`;
